import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from "react-bootstrap";




export default class Calculator extends React.Component {
    value!: Number; 
    years!: Number;
    rate!: Number;

    constructor(props: any) {
        super(props);
    }

  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">
        <h1>Calculator</h1>
        <Container>
            <div className="form-group">
                <label htmlFor="value">Value</label>
                <input type="number" className="form-control" id="value" aria-describedby="value" placeholder="Enter value" />

                <label htmlFor="years">Years</label>
                <input type="number" className="form-control" id="years" aria-describedby="years" placeholder="Enter years" />

                <label htmlFor="rate">Rate</label>
                <input type="number" className="form-control" id="rate" aria-describedby="rate" placeholder="Enter rate" />

                <button type="submit" className="btn btn-primary">Submit</button>
            </div>
        </Container>
      </div>
    );
  }
}
