import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import { Container } from "react-bootstrap";




export default class Leaderboard extends React.Component {
  leaderBoard = [] as any;
  season = '';

  leaderBoardUpdate(){
    fetch(`https://us.api.blizzard.com/data/wow/pvp-season/${this.season}/pvp-leaderboard/3v3?namespace=dynamic-us&locale=en_US&access_token=USHEAP6FBBr3rc7lHsAExJRZEL3v55bcVu`)
    .then(response => response.json())
    .then(data => {
      this.leaderBoard = data.entries;
      if (this.leaderBoard.length > 0) {
        this.createTableWithData();
      }
    }
    )
  }
  componentDidMount() {
    this.leaderBoardUpdate();
  } 
  createTableWithData() {
    console.log(this.leaderBoard[0]);
    var table = document.getElementsByTagName("tbody")[0];
    // just rank and name
    for (var i = 0; i < this.leaderBoard.length; i++) {
      var row = table.insertRow(i);
      var rank = row.insertCell(0);
      var name = row.insertCell(1);
      var rating = row.insertCell(2);
      rank.innerHTML = this.leaderBoard[i].rank;
      name.innerHTML = this.leaderBoard[i].character.name;
      rating.innerHTML = this.leaderBoard[i].rating;
      // if name is equal to current user, highlight row
      if (this.leaderBoard[i].character.name === "Truck") {
        row.classList.add("table-success");
      }
    }
  }




  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">

        <Container>
        <Button onClick={this.leaderBoardUpdate} variant="primary">Refresh</Button>
        <select onChange={(e) => {this.season = e.target.value; this.leaderBoardUpdate();}}>
          <option value="">Select Season</option>
          <option value="27">Season 27</option>
          <option value="28">Season 28</option>
          <option value="29">Season 29</option>
          <option value="30">Season 30</option>
          <option value="31">Season 31</option>
          <option value="32">Season 32</option>


        </select>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Name</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
          </tbody>
        </table>
        </Container>
      </div>
    );
  }
}
