import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from "react-bootstrap";




export default class Leaderboard extends React.Component {
  // fetch from api and display the leaderboard
  leaderBoardUpdate(){
    fetch('https://us.api.blizzard.com/data/wow/pvp-season/27/pvp-leaderboard/3v3?namespace=dynamic-us&locale=en_US&access_token=USHEAP6FBBr3rc7lHsAExJRZEL3v55bcVu')
    .then(response => response.json())
    .then(data => {
      console.log(data);
    }
    )
  }
  componentDidMount() {
    this.leaderBoardUpdate();
  } 

  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">
        <Container>
        <div className="jumbotron m-auto d-flex justify-content-center">
        <div className="h1">Arena Help</div>
        <button onClick={this.leaderBoardUpdate}>Refresh</button>
      </div>
        </Container>
      </div>
    );
  }
}
