import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import { Container } from "react-bootstrap";
import { couldStartTrivia } from 'typescript';




export default class Leaderboard extends React.Component {
  leaderBoard = [] as any;
  leaderBoardUpdate(){
    fetch('https://us.api.blizzard.com/data/wow/pvp-season/27/pvp-leaderboard/3v3?namespace=dynamic-us&locale=en_US&access_token=USHEAP6FBBr3rc7lHsAExJRZEL3v55bcVu')
    .then(response => response.json())
    .then(data => {
      this.leaderBoard = data.entries;
      if (this.leaderBoard.length > 0) {
        this.createTableWithData();
      }
    }
    )
  }
  componentDidMount() {
    this.leaderBoardUpdate();
  } 
  createTableWithData() {
    console.log(this.leaderBoard[0]);
    var table = document.getElementsByTagName("tbody")[0];
    // just rank and name
    for (var i = 0; i < this.leaderBoard.length; i++) {
      var row = table.insertRow(i);
      var rank = row.insertCell(0);
      var name = row.insertCell(1);
      var rating = row.insertCell(2);
      rank.innerHTML = this.leaderBoard[i].rank;
      name.innerHTML = this.leaderBoard[i].character.name;
      rating.innerHTML = this.leaderBoard[i].rating;
      // if name is equal to current user, highlight row
      if (this.leaderBoard[i].character.name === "Truck") {
        row.classList.add("table-success");
      }
    }
  }


  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">
        <Container>
        <Button onClick={this.leaderBoardUpdate} variant="primary">Refresh</Button>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Name</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
          </tbody>
        </table>
        </Container>
      </div>
    );
  }
}
