import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import { Container } from "react-bootstrap";




export default class Leaderboard extends React.Component {
  leaderBoard = {} as any;
  leaderBoardUpdate(){
    fetch('https://us.api.blizzard.com/data/wow/pvp-season/27/pvp-leaderboard/3v3?namespace=dynamic-us&locale=en_US&access_token=USHEAP6FBBr3rc7lHsAExJRZEL3v55bcVu')
    .then(response => response.json())
    .then(data => {
      this.leaderBoard = data;
      console.log(this.leaderBoard);
    }
    )
  }
  componentDidMount() {
    this.leaderBoardUpdate();
  } 
  createTableWithData() {
    let table = [];
    for (let i = 0; i < this.leaderBoard.pvp_leaderboard.length; i++) {
      let row = [];
      row.push(this.leaderBoard.pvp_leaderboard[i].rank);
      row.push(this.leaderBoard.pvp_leaderboard[i].character.name);
      row.push(this.leaderBoard.pvp_leaderboard[i].character.realm.name);
      row.push(this.leaderBoard.pvp_leaderboard[i].character.faction.name);
      row.push(this.leaderBoard.pvp_leaderboard[i].character.class.name);
      row.push(this.leaderBoard.pvp_leaderboard[i].character.spec.name);
      table.push(row);
    }
    return table;
  }


  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">
        <Container>
        <Button onClick={this.leaderBoardUpdate} variant="primary">Refresh</Button>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Name</th>
              <th>Realm</th>
              <th>Faction</th>
              <th>Class</th>
              <th>Spec</th>
            </tr>
          </thead>
          <tbody>
            {this.createTableWithData().map((row, index) => {
              return (
                <tr key={index}>
                  {row.map((cell, index) => {
                    return <td key={index}>{cell}</td>;
                  }
                  )}
                </tr>
              );
            }
            )}
          </tbody>
        </table>
        </Container>
      </div>
    );
  }
}
