import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from "react-bootstrap";




export default class Calculator extends React.Component {
    value = 0;
    years = 0;
    rate = 0;
    principal = 0;
    result = 0;

    calculate = () => {
        let value = document.getElementById("value") as HTMLInputElement;
        this.value = Number(value.value);
        let years = document.getElementById("years") as HTMLInputElement;
        this.years = Number(years.value);
        let rate = document.getElementById("rate") as HTMLInputElement;
        this.rate = Number(rate.value) / 100;

        this.result = this.value * Math.pow(1 + this.rate, this.years);
    }
  render() {
    return (
      <div className="jumbotron m-auto d-flex justify-content-center">
        <Container>
            <div className="form-group">
                <label htmlFor="value">Value</label>
                <input type="number" className="form-control" id="value" aria-describedby="value" placeholder="Enter value" />

                <label htmlFor="years">Years</label>
                <input type="number" className="form-control" id="years" aria-describedby="years" placeholder="Enter years" />

                <label htmlFor="rate">Rate</label>
                <input type="number" className="form-control" id="rate" aria-describedby="rate" placeholder="Enter rate" />

                <button type="submit" onClick={this.calculate} className="btn btn-primary">Submit</button>
            </div>
            <div className="font size">${this.result}</div>
        </Container>
      </div>
    );
  }
}
